    def connectSignals(self, presenter):
        # Buttons
        self.button_ch1_in.clicked.connect(presenter.on_ch1_in_clicked)
        self.button_ch2_in.clicked.connect(presenter.on_ch2_in_clicked)
        self.loadButton.clicked.connect(presenter.on_load_single_clicked)
        self.loadComputeButton.clicked.connect(presenter.on_load_all_and_compute_clicked)
        self.saveButton.clicked.connect(presenter.on_save_clicked)
        self.computeButton.clicked.connect(presenter.on_compute_clicked)

        self.button_save_path_ch1.clicked.connect(presenter.on_save_path_ch1_clicked)
        self.button_save_path_ch2.clicked.connect(presenter.on_save_path_ch2_clicked)

        self.lineEdit_ch1_in.textChanged.connect(presenter.on_ch1_in_changed)
        self.lineEdit_ch2_in.textChanged.connect(presenter.on_ch2_in_changed)
        # ComboBoxes
        self.comboBox_technique.currentIndexChanged.connect(lambda idx: presenter.on_technique_changed(idx))
        self.referenceComboBox.currentIndexChanged.connect(lambda idx: presenter.on_reference_changed(idx))

        # SpinBoxes (Darkfield)
        self.spinBox_darkfield_rho1.valueChanged.connect(presenter.on_darkfield_rho1_changed)
        self.spinBox_darkfield_rho2.valueChanged.connect(presenter.on_darkfield_rho2_changed)
        # SpinBoxes (Phase-Shifted Darkfield)
        self.spinBox_psdarkfield_rho1.valueChanged.connect(presenter.on_psdarkfield_rho1_changed)
        self.spinBox_psdarkfield_rho2.valueChanged.connect(presenter.on_psdarkfield_rho2_changed)
        self.spinBox_psdarkfield_phaseshift.valueChanged.connect(presenter.on_psdarkfield_phaseshift_changed)
        # SpinBoxes (Intensity Weighted Darkfield)
        self.spinBox_iwdarkfield_rho1.valueChanged.connect(presenter.on_iwdarkfield_rho1_changed)
        self.spinBox_iwdarkfield_rho2.valueChanged.connect(presenter.on_iwdarkfield_rho2_changed)
        # SpinBoxes (Spiral Darkfield)
        self.spinBox_sdarkfield_rho1.valueChanged.connect(presenter.on_sdarkfield_rho1_changed)
        self.spinBox_sdarkfield_rho2.valueChanged.connect(presenter.on_sdarkfield_rho2_changed)
        self.spinBox_sdarkfield_m.valueChanged.connect(presenter.on_sdarkfield_m_changed)

