def main():
    from .phasePresenter import PhasePresenter
    from .phaseModel import PhaseModel
    app = QtWidgets.QApplication(sys.argv)
    MainWindow = Window()
    model = PhaseModel()
    presenter = PhasePresenter(MainWindow.ui, model)
    MainWindow.ui.connectSignals(presenter)
    MainWindow.ui.set_spinbox_defaults()
    MainWindow.ui.set_tooltips()
    
    MainWindow.show()
    sys.exit(app.exec_())
